import { reqCategoryList } from "@/api"
const state = {}
const mutations = {
    CA
}
const actions = {
    async categoryList(){
        let result = await reqCategoryList()
            if(result.code==200){
                commit("CATEGORYLIST",result.data)
            }
    }
}
const getters = {}

export default {
    state,
    mutations,
    actions,
    getters
}
