import { reqCartList, reqDeleteCartById, reqUpdateCheckedByid } from "@/api"
import { refreshStyles } from "less"


const state = {
    getCartList:[]
}
const mutations = {
    GETCARTLIST(state,getCartList){
        state.getCartList = getCartList
    }
    
}
const actions = {
    async getCartList({commit}){
        let result = await reqCartList()
        if(result.code == 200){
            commit('GETCARTLIST',result.data)
        }
    },
    async deleteCartListBySkuId({commit},skuid){
        let result = await reqDeleteCartById(skuid)
        if(result.code==200){
            return 'ok'
        }else{
            return Promise.reject(new Error('fail'))
        }
    },
    async updateCheckedById({commit},{skuid,isChecked}){
        let result = await reqUpdateCheckedByid(skuid,isChecked)
        if(result.code==200){
            return 'ok'
        }else{
            return Promise.reject(new Error('fail'))
        }
    },
    async deleteAllCheckedCart(dispatch,getters){
        let PromiseAll = []
        getters.getCartList.forEach(item => {
            let promise = item.isChecked == 1?dispatch('deleteCartListBSkuId',item.skuid):''
            PromiseAll.push(promise)
        });
        return promise.all(PromiseAll)
    } ,  
    async updateAllCartChecked(dispatch,state){
        let PromiseAll = []
        getters.getCartList.forEach(item => {
            let promise = item.isChecked == 1?dispatch('deleteCartListBSkuId',item.skuid):''
            PromiseAll.push(promise)
        });
        return promise.all(PromiseAll)
    } ,  
}
const getters = {
    getCartList(state){
        return state.getCartList || []
    },
}

export default {
    state,
    mutations,
    actions,
    getters
}
