import Search from '@/pages/Search'
import Login from '@/pages/Login'
import Register from '@/pages/Register'
import Detail from '@/pages/Detail'
import AddCartSuccess from '@/pages/AddCartSuccess'
import ShopCart from '@/pages/ShopCart'
import Trade from '@/pages/Trade'
import Pay from '@/pages/Pay'
import PaySuccess from '@/pages/PaySuccess'

import MyOrder from '@/pages/Center/myOrder'
import GroupOrder from '@/pages/Center/groupOrder'

    


export default [
        {
            path:"/home",
            component:() =>import('@/pages/Home'),
            meta:{show:true}
        },
        {
            path:"/center",
            component:() =>import('@/pages/Center'),
            meta:{show:true},
            children:[
                {
                    path:'myorder',
                    component:MyOrder
                },
                {
                    path:'grouporder',
                    component:GroupOrder
                }
            ]
        },
        {
            path:"/detail/:skuid",
            component:Detail,
            meta:{show:true}
        },
        {
            path:"/addcartsuccess",
            component:AddCartSuccess,
            name:'addcartsuccess',
            meta:{show:true}
        },
        {
            path:"/paysuccess",
            component:PaySuccess,
            name:'paysuccess',
            meta:{show:true}
        },
        {
            path:"/shopCart",
            component:ShopCart,
            name:'shopCart',
            meta:{show:true}
        },
        {
            path:"/trade",
            component:Trade,
            name:'trade',
            meta:{show:true},
            beforeEnter: (to, from, next) => {
                if(from.path == '/shopcart'){
                    next()
                }else{
                    next(false)
                }
            }
        },
        {
            path:"/pay",
            component:Pay,
            name:'pay',
            meta:{show:true},
            beforeEnter: (to, from, next) => {
                if(from.path == '/trade'){
                    next()
                }else{
                    next(false)
                }
            }
        },
        {
            path:"/search/:keyword?",
            component:Search,
            meta:{show:true},
            name:"search"
        },
        {
            path:"/login",
            component:Login
        },
        {
            path:"/register",
            component:Register
        },
        //重定向，在项目跑起来的时候，访问/，立马让他定向到首页
        {
            path:'*',
            redirect:"/home"
        },
    ]
