//当前这个模块，api进行统一管理

import requests from "./ajax";
import mockRequests from "./mockAjax"

export const reqCategoryList = () => 
     requests.get('http://gmall-h5-api.atguigu.cn/api/product/getBaseCategoryList')


export const reqGetBannerList = () => 
     mockRequests.get('/mock/banner')


export const reqFloorList = () => 
     mockRequests.get('/mock/floor')

    
export const reqGetSearchInfo = (params) => 
     requests({url:'http://gmall-h5-api.atguigu.cn/api/list',method:'post',data:params})

// export const reqGoodInfo = (skuid) => 
//      mockRequests.get('/mock/item')

export const reqGoodInfo = (skuid) => 
     requests({url:`http://gmall-h5-api.atguigu.cn/api/item/${skuid}`,method:'get'})

export const reqAddOrUpdateShopCart = (skuid,skuNum) =>
     requests({url:`http://gmall-h5-api.atguigu.cn/api/cart/addToCart/${skuid}/${skuNum}`,method:'post'})


// export const reqCartList = () => 
//      requests.get('http://gmall-h5-api.atguigu.cn/api/cart/cartList')

export const  reqCartList = () => 
     mockRequests.get('/mock/cartList')

export const reqDeleteCartById = (skuid) =>requests({url:`http://gmall-h5-api.atguigu.cn/api/cart/deleteCart/${skuid}`,method:'delete'})

export const reqUpdateCheckedByid = (skuid,isChecked) =>
     requests({url:`http://gmall-h5-api.atguigu.cn/api/cart/checkCart/${skuid}/${isChecked}`,method:'get'})

export const reqGetCode = (phone) => requests.get()
