import { reqCartList } from "@/api"


const state = {
    carlist:[]
}
const mutations = {
    GETCARTLIST(state,carlist){
        state.carlist = carlist
    }
    
}
const actions = {
    async getCartList({commit}){
        let result = await reqCartList()
        if(result.code == 200){
            commit('GETCARTLIST',result.data)
        }
    }
}
const getters = {
   carlist(state){
    return state.carlist[0] || {}
   },
 
}

export default {
    state,
    mutations,
    actions,
    getters
}
