import { reqGoodInfo } from "@/api"



const state = {
    goodInfo:{}
}

const mutations = {
    GETGOODINFO(state,getGoodInfo){
        state.goodInfo = getGoodInfo
    }
}

const actions = {
    async getGoodInfo({commit},skuid){
        let result = await reqGoodInfo()
            if(result.code==200){
                commit("GETGOODINFO",result.data)
            }
    },
}
const getters = {}

export default {
    state,
    mutations,
    actions,
    getters
}
