//当前这个模块，api进行统一管理

import requests from "./ajax";
import mockRequests from "./mockAjax"

export const reqCategoryList = () => {
    return requests.get('http://gmall-h5-api.atguigu.cn/api/product/getBaseCategoryList')
}

export const reqGetBannerList = () => {
    return mockRequests.get('/mock/banner')
}

export const reqFloorList = () => {
    return mockRequests.get('/mock/floor')
}
    
export const searchList = () => {
    return requests.post('http://gmall-h5-api.atguigu.cn/api/list')
}
