import { reqGetCode } from "@/api";
const state = {}


const mutations = {}


const actions = {
    async getCode({ commit }, phone) {
        let result = await reqGetCode(phone)
        if(result.code==200){
            commit('GETCODE',result.data)
        }else {
            return Promise.reject(new R)
        }
    }
}



const getters = {}


export default {
    state,
    mutations,
    actions,
    getters
}
