import { reqGoodInfo } from "@/api"



const state = {
    getGoodInfo:{}
}

const mutations = {
    GETGOODINFO(state,getGoodInfo){
        state.getGoodInfo = getGoodInfo
    }
}

const actions = {
    async getGoodInfo({commit}){
        let result = await reqGoodInfo()
            if(result.code==200){
                commit("GETGOODINFO",result.data)
            }
    },
}
const getters = {
    categoryView(state){
        return state.getGoodInfo.categoryView || {}
    },
    skuInfo(state){
        return state.getGoodInfo.skuInfo || {}
    },
    spuSaleAttrList(state){
        return state.getGoodInfo.spuA
    }
}

export default {
    state,
    mutations,
    actions,
    getters
}
